mapper.update:
      UPDATE PROFESSOR
         SET PHONE = #{phone},
			 UPD_DTM = SYSDATE(),
			 UPD_USR = #{professorId}	
       WHERE PROFESSOR_ID = #{professorId}

      UPDATE PROFESSOR
         SET EMAIL = #{email},
         	 UPD_DTM = SYSDATE(),
			 UPD_USR = #{professorId}	
       WHERE PROFESSOR_ID = #{professorId}
