mapper.update:
UPDATE STUDENT
	SET PHONE = #{phone},
		UPD_DTM = SYSDATE(),
		UPD_USR = #{studentId}	
WHERE STUDENT_ID = #{studentId}

UPDATE STUDENT
	SET EMAIL = #{email},
		UPD_DTM = SYSDATE(),
		UPD_USR = #{studentId}			
WHERE STUDENT_ID = #{studentId}

mapper.select:
SELECT E.CLASS_CODE AS classCode, C.CLASS_NAME AS className, C.CLASS_TYPE AS classType, S.STUDENT_ID AS studentId, S.NAME AS studentName, E.STUDENT_YEAR AS studentYear, E.SEMESTER AS semester, E.MIDDLE_TEST AS middleTest, E.FINAL_TEST AS finalTest, E.REPORT AS report, E.SCORE AS score, E.GRADE AS grade FROM ENROLLMENT E INNER JOIN STUDENT S ON S.STUDENT_ID = E.STUDENT_ID INNER JOIN CLASS C ON C.CLASS_CODE = E.CLASS_CODE WHERE S.STUDENT_ID = #{studentId}
AND E.STUDENT_YEAR = #{year} AND E.SEMESTER = #{semester}
ORDER BY studentYear DESC, semester DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM ENROLLMENT E
INNER JOIN STUDENT S ON S.STUDENT_ID = E.STUDENT_ID
INNER JOIN CLASS C ON C.CLASS_CODE = E.CLASS_CODE
WHERE
S.STUDENT_ID = #{studentId}

	AND E.STUDENT_YEAR = #{year}
	AND E.SEMESTER = #{semester}

SELECT UNIV_YEAR AS STUDENT_YEAR
FROM STUDENT
WHERE STUDENT_ID = #{studentId};
